mapper.select:
SELECT *, ( SELECT count(*) FROM comments WHERE post_id = posts.id AND isDeleted = 0) as commentCount FROM posts WHERE isDeleted = 0
AND title like CONCAT('%', #{search}, '%')
AND writer like CONCAT('%', #{search}, '%')
AND category = #{category}
ORDER BY ctime DESC LIMIT #{start}, 20;

mapper.update:
UPDATE comments
SET isDeleted = 1
WHERE post_id = #{id};

UPDATE posts
SET isDeleted = 1
WHERE id = #{id};

UPDATE posts
SET title = #{title}, content = #{content}, writer = #{writer}, password = #{password}, category = #{category}, mtime = #{mtime}
WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM posts
WHERE isDeleted = 0;
